import React, { Component } from 'react'
import Axios from 'axios'
import App from '../App'
import '../App.css'
export default class Homepage extends Component {
  constructor(props) {
    super(props)
    this.state = {
      data: [],
      loading: true,
    }
  }

  componentDidMount() {
    Axios.get(`https://itunes.apple.com/us/rss/topalbums/limit=100/json`)
      .then((res) => {
        console.log(res)
        this.setState({
          data: res.data.feed.entry,
          loading: false,
        })
      })
      .catch((err) => console.log(err))
  }

  onhh = () => {
    console.log(this.state.data[0])
  }

  render() {
    return (
      <div className='cont'>
        {this.state.loading == false ? (
          <div className='row mt-4'>
            {this.state.data.map((song) => {
              return (
                <div className='col-12 col-sm-6  col-md-4 col-lg-3 mb-5'>
                  <div
                    class='card '
                    style={{ width: '18em', maxHeight: '100vh' }}
                  >
                    <img
                      class='img-fluid '
                      src={song['im:image'][2].label}
                      alt='...'
                    />
                    <div class='card-body'>
                      <h5 class='card-title'>{song['im:name'].label}</h5>
                      <p class='card-text'>{song['im:artist'].label}</p>
                      <a href='#' class='btn btn-primary'>
                        check me
                      </a>
                    </div>
                  </div>
                </div>
              )
            })}
          </div>
        ) : (
          ''
        )}
      </div>
    )
  }
}
